          fill: false,
          pointRadius:0
        },
      ],
    };

    // Chart options
    var options = {
      responsive: true,
      title: {
        display: true,
        text: "Multiple Lines Graph Example",
      },
      scales: {
        yAxes: [
          {
            ticks: {
              beginAtZero: true,
            },
          },
        ],
        x: {
          grid: {
            display: false,
          },
        },
      },
      plugins: {
        legend: {
          position: 'top',
          align: 'end',
          labels: {
            usePointStyle: true,
            pointStyle: "circle",
            pointStyleWidth: 6,
            boxWidth: 4,
            boxHeight: 4
          },
        },
      },
    };

    // Create the chart
    var ctx = document.getElementById("myChart").getContext("2d");
    var myChart = new Chart(ctx, {
      type: "line",
      data: data,
      options: options,
    });
  </script>

